import React, { useState } from 'react';
import { FaStar } from 'react-icons/fa';

function Testimonial() {
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ name: '', location: '', message: '', rating: 0 });

  const handleSubmit = () => {
    setReviews([...reviews, newReview]);
    setNewReview({ name: '', location: '', message: '', rating: 0 });
    alert('Thank you for your review! A confirmation message has been sent to your WhatsApp.');
    // Integrate WhatsApp API here for sending thank-you message
  };

  return (
    <div>
      <div className="container-fluid bg-primary py-5 mb-5 hero-header">
        <div className="container py-5">
          <div className="row justify-content-center py-5">
            <div className="col-lg-10 pt-lg-5 mt-lg-5 text-center">
              <h1 className="display-3 text-white animated slideInDown">Customer Reviews</h1>
              <nav aria-label="breadcrumb">
                <ol className="breadcrumb justify-content-center">
                  <li className="breadcrumb-item"><a href="/">Home</a></li>
                  <li className="breadcrumb-item text-white active" aria-current="page">Customer Reviews</li>
                </ol>
              </nav>
            </div>
          </div>
        </div>
      </div>
      {/* Reviews Section */}
      <div className="container-xxl py-5 wow fadeInUp" data-wow-delay="0.1s">
        <div className="container">
          <div className="text-center">
            <h6 className="section-title bg-white text-center text-primary px-3">Reviews</h6>
            <h1 className="mb-5">What Our Customers Say!!!</h1>
          </div>
          <div className="testimonial-carousel position-relative">
            {reviews.map((review, index) => (
              <div key={index} className="testimonial-item bg-white text-center border p-4">
                <img alt='customer' className="bg-white rounded-circle shadow p-1 mx-auto mb-3" src="assets/img/user-placeholder.jpg" style={{ width: 80, height: 80 }} />
                <h5 className="mb-0">{review.name}</h5>
                <p>{review.location}</p>
                <p className="mb-0">{review.message}</p>
                <div className="d-flex justify-content-center">
                  {[...Array(5)].map((_, i) => (
                    <FaStar key={i} color={i < review.rating ? "#ffc107" : "#e4e5e9"} />
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      {/* Review Submission Form */}
      <div className="container py-5">
        <div className="text-center">
          <h2 className="mb-4">Submit Your Review</h2>
        </div>
        <div className="row justify-content-center">
          <div className="col-lg-6">
            <input type="text" className="form-control mb-3" placeholder="Your Name" value={newReview.name} onChange={(e) => setNewReview({ ...newReview, name: e.target.value })} />
            <input type="text" className="form-control mb-3" placeholder="Location" value={newReview.location} onChange={(e) => setNewReview({ ...newReview, location: e.target.value })} />
            <textarea className="form-control mb-3" placeholder="Your Review" value={newReview.message} onChange={(e) => setNewReview({ ...newReview, message: e.target.value })}></textarea>
            <div className="d-flex justify-content-center mb-3">
              {[...Array(5)].map((_, i) => (
                <FaStar key={i} color={i < newReview.rating ? "#ffc107" : "#e4e5e9"} onClick={() => setNewReview({ ...newReview, rating: i + 1 })} style={{ cursor: "pointer" }} />
              ))}
            </div>
            <button className="btn btn-primary w-100" onClick={handleSubmit}>Submit Review</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Testimonial;
